(function() {
  'use strict';
  var $, ie6;

  $ = jQuery;

  ie6 = !!window.ActiveXObject && !window.XMLHttpRequest;

  $.extend({
    setRepeater: function(fn, interval) {
      setTimeout((function() {
        fn();
        $.setRepeater(fn, interval);
      }), interval);
    },
    setTimer: function(fn, interval) {
      var recurse, ref;
      ref = {};
      ref["continue"] = true;
      (recurse = function() {
        if (ref["continue"]) {
          ref.timeout = setTimeout((function() {
            fn();
            recurse();
          }), interval);
        }
      })();
      return ref;
    },
    clearTimer: function(ref) {
      ref["continue"] = false;
      clearTimeout(ref.timeout);
    }
  });

  $.extend({
    effect: {
      smog: function($now, $next, speed) {
        if (speed == null) {
          speed = 200;
        }
        $now.fadeOut(speed, function() {
          $next.fadeIn(speed);
        });
      }
    }
  });

  $.fn.extend({
    promo: function(exchange, interval) {
      var $items, $nums, $this, l, num, timer;
      if (exchange == null) {
        exchange = $.effect.smog;
      }
      if (interval == null) {
        interval = 5000;
      }
      /*
      			<div id="selector">
      				<ul class="items">
      					<li><a href=""><img src="" alt=""></a></li>
      					<li><a href=""><img src="" alt=""></a></li>
      					<li><a href=""><img src="" alt=""></a></li>
      				</ul>
      				<ol class="nums">
      					<li class="cur">1</li>
      					<li>2</li>
      					<li>3</li>
      				</ol>
      				<input type="button" class="pre">
      				<input type="button" class="next">
      			</div>
      */

      $this = $(this);
      $items = $this.find('.items>li');
      $nums = $this.find('.nums>li');
      l = $nums.length;
      num = 1 + l;
      $nums.click(function() {
        if (!ie6) {
          $nums.filter('[class*="cur"]').removeClass('cur');
          $(this).addClass('cur');
        }
        num = +$(this).text();
        exchange($items.filter(':visible'), $items.eq(num - 1));
      });
      $this.find('.pre').click(function() {
        $nums.eq(num % l - 2).click();
      });
      $this.find('.next').click(function() {
        $nums.eq(num % l).click();
      });
      timer = null;
      $this.hover((function() {
        $.clearTimer(timer);
      }), (function() {
        timer = $.setTimer((function() {
          $nums.eq(num % l).click();
        }), interval);
      }));
      $this.mouseout();
      $items.hide().eq(0).show();
      return this;
    },
    picasa: function() {
      var $box, $doc, $img, $lab;
      if (!$('#picasaBox').length) {
        $('<div id="picasaBox"></div>').css({
          'width': 0,
          'height': 0,
          'background-image': 'url(data:image/png;base64,)',
          'position': 'absolute',
          'left': '50%',
          'display': 'none',
          'z-index': 9999
        }).append($('<img src="" alt="">').css({
          'position': 'absolute',
          'top': '50%',
          'left': '50%'
        })).appendTo('body');
        $('<img id="picasaLab" src="" alt="">').hide().appendTo('body');
      }
      $box = $('#picasaBox');
      $img = $('#picasaBox>img');
      $lab = $('#picasaLab');
      $doc = $(document);
      $(window).scroll(function() {
        $box.css({
          'top': $doc.scrollTop()
        });
      });
      $box.click(function() {
        var bmt;
        bmt = $box.height() / 2;
        $box.animate({
          'height': 0,
          'width': 0,
          'left': '50%',
          'margin-top': bmt
        }, 400, $box.hide);
        $img.animate({
          'height': 0,
          'width': 0,
          'margin': 0
        }, 400, $img.hide);
      });
      $(this).filter('img').click(function() {
        var ih, iml, imt, iw;
        $img.attr('src', $(this).attr('src'));
        $lab.attr('src', $(this).attr('src'));
        ih = $lab.height();
        imt = ih / -2;
        iw = $lab.width();
        iml = iw / -2;
        $box.show().css({
          'top': $doc.scrollTop(),
          'margin-top': $(window).height() / 2
        }).animate({
          'height': '100%',
          'width': '100%',
          'left': 0,
          'margin-top': 0
        });
        $img.css({
          'height': 0,
          'width': 0
        }).show().animate({
          'height': ih,
          'width': iw,
          'margin-top': imt,
          'margin-left': iml
        });
      });
      return this;
    },
    showpic: function(target, easing, auto, speed) {
      var $imgs, $list, $tar, autoShow, l, row;
      if (auto == null) {
        auto = false;
      }
      if (speed == null) {
        speed = 5000;
      }
      $list = $(this);
      $tar = $(target);
      $imgs = $list.find('img');
      l = $imgs.length;
      row = Math.floor($list.height() / $list.parent().height());
      autoShow = function() {
        var $img;
        $img = $imgs.eq(++autoShow.num % l);
        $img.click();
        if (autoShow.num % row === 0 && $img.is(':visible')) {
          $list.parent().siblings().find('.next').click();
        }
        if (autoShow.num % l === 0) {
          $list.css({
            'margin-top': 0
          });
        }
      };
      autoShow.num = 0;
      $imgs.click(function() {
        var $that;
        $that = $(this);
        autoShow.num = $that.parent().index();
        $list.find('li').filter('.cur').removeClass('cur');
        $that.parent().addClass('cur');
        $tar.fadeOut(easing, function() {
          $(this).attr('src', $that.attr('src')).fadeIn(easing);
        });
      });
      if (auto) {
        $.setRepeater(autoShow, speed);
      }
      return this;
    }
  });

}).call(this);
